mapper.update:
UPDATE linkjobs_cpmember SET cpmember_license_no = #{cpMember.cpMemberLicenseNo}, cpmember_manager = #{cpMember.cpMemberManager}, cpmember_company = #{cpMember.cpMemberCompany}, cpmember_ceo = #{cpMember.cpMemberCeo}, cpmember_email = #{cpMember.cpMemberEmail},
industry_no = #{cpMember.cpMemberIndustryNo},
cpmember_establish_date = #{cpMember.cpMemberEstablishDate},
area2_no = (SELECT area2_no FROM linkjobs_area2 WHERE area2_gungu = #{areaGungu} AND area_no = (SELECT area_no FROM linkjobs_area WHERE area_sido = #{areaSido})),
cpmember_detailed_address = #{cpMember.cpMemberDetailedAddress}, cpmember_phonenum = #{cpMember.cpMemberPhonenum}, cpmember_number_of_employees = #{cpMember.cpMemberNumberOfEmployees}
WHERE cpmember_id = #{cpMember.cpMemberId}

UPDATE linkjobs_cpmember
SET cpmember_pw = PASSWORD(#{cpMemberPw})
WHERE 	cpmember_id = #{cpMemberId}
AND	cpmember_license_no = #{cpMemberLicenseNo}
AND cpmember_manager = #{cpMemberManager} 
AND cpmember_email = #{cpMemberEmail}

mapper.select:
SELECT cpmember_id AS cpMemberId, cpmember_email AS cpMemberEmail 
FROM linkjobs_cpmember 
WHERE cpmember_license_no = #{cpMemberLicenseNo}
AND cpmember_manager = #{cpMemberManager} 
AND cpmember_email = #{cpMemberEmail}
