mapper.update:
UPDATE linkjobs_resume
SET	
	resume_avaliability = #{resumeAvaliability}
WHERE resume_no = #{resumeNo} 
AND member_id = #{memberId};

mapper.select:
SELECT count(*)
FROM linkjobs_resume
WHERE member_id = #{memberId}

mapper.delete:
DELETE FROM linkjobs_resume
WHERE member_id = #{memberId} 
AND resume_no = #{resumeNo};
